import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import WaveformCanvas from './WaveformCanvas';
import './style.css';

const Form = () => {
    const [activeForm, setActiveForm] = useState('login');
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const switchForm = () => {
        if (activeForm === 'login') {
            setActiveForm('signUp');
        } else {
            setActiveForm('login');
        }
    };

    return (
        <div className="container">
            <WaveformCanvas />
            <div className="form-container">
                <div id="loginForm" className={`form-side ${activeForm === 'login' ? 'active' : ''}`}>
                    <h2>Login</h2>
                    <input
                        type="email"
                        className="input"
                        placeholder="Username / Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input
                        type="password"
                        className="input"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button className="button">Login</button>
                </div>
                <div id="signUpForm" className={`form-side ${activeForm === 'signUp' ? 'active' : ''}`}>
                    <h2>Sign Up</h2>
                    <input
                        type="text"
                        className="input"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="email"
                        className="input"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input
                        type="password"
                        className="input"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button className="button">Sign Up</button>
                </div>
                <div id="forgotPasswordForm" className={`form-side ${activeForm === 'forgotPassword' ? 'active' : ''}`}>
                    <h2>Forgot Password</h2>
                    <input
                        type="email"
                        className="input"
                        placeholder="Enter your email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <button className="button">Submit</button>
                </div>
                <button className="switch-button" onClick={switchForm}>
                    {activeForm === 'login' ? "Don't have an account? Sign Up" : 'Already have an account? Login'}
                </button>
                {activeForm === 'login' && (
                    <a id="forgotPasswordLink" href="#" onClick={() => setActiveForm('forgotPassword')}>
                        Forgot Password?
                    </a>
                )}
            </div>
        </div>
    );
};

export default Form;
