import React, { useState } from 'react';

const VideoModal = () => {
  const [openVideoModal, setOpenVideoModal] = useState(false);

  return (
    <>
      {/* Trigger Button */}
      <button onClick={() => setOpenVideoModal(true)}>Open Video Modal</button>

      {/* Video Popup Modal */}
      {openVideoModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          <div className="relative max-w-3xl mx-auto rounded-lg overflow-hidden shadow-lg bg-white" onClick={(e) => e.stopPropagation()}>
            <img src="./Kenya Power PLC_files/banner.jpeg" alt="Video Placeholder" className="w-full h-full object-cover" />
            <button onClick={() => setOpenVideoModal(false)} className="absolute top-4 right-4 p-2 bg-gray-200 rounded-full text-black">
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" className="w-6 h-6">
                <path fillRule="evenodd" d="M6.293 6.293a1 1 0 0 1 1.414 0L10 8.586l2.293-2.293a1 1 0 0 1 1.414 1.414L11.414 10l2.293 2.293a1 1 0 0 1-1.414 1.414L10 11.414l-2.293 2.293a1 1 0 0 1-1.414-1.414L8.586 10 6.293 7.707a1 1 0 0 1 0-1.414z" clipRule="evenodd"></path>
              </svg>
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default VideoModal;
