import React, { useState } from 'react';
import 'aos/dist/aos.css';
import AOS from 'aos';

AOS.init();

const CardComponent = ({ siteGrayscale }) => {
  return (
    <div data-aos="flip-left" className="col-span-1 flex flex-col gap-4 group">
      <div
        className="w-full h-52 bg-green rounded-xl bg-cover bg-top transition-all duration-300 group-hover:scale-105"
        style={{
          backgroundImage: `url('https://kenyapower.belvadigital.com/storage/01J2BVPBVGTPMT5KXJH264ZVEX.webp')`,
          filter: siteGrayscale ? 'grayscale(100%)' : 'none',
        }}
      ></div>
      <div className="border-b border-green"></div>
      <div className="flex flex-1 flex-col gap-1">
        <div className="font-serif text-xl font-normal text-primary dark:text-slate-100 flex-shrink-0 group-hover:text-green">
          New Customers
        </div>
        <div className="text-sm font-sans leading-7 text-slate-500 dark:text-slate-500 newsarticle flex-1">
          <p>
            If you're looking to get electricity for your home or business (with power needs below 25kVA for homes and above 25kVA for bigger setups, but not including small to medium businesses with loads between 100kVA to 200kVA), this process is for you.
          </p>
        </div>
        <div className="flex mt-2 flex-shrink-0">
          <a
            href="https://kenyapower.belvadigital.com/customer-support"
            className="btn relative inline-flex items-center justify-start overflow-hidden font-medium transition-all bg-primary rounded hover:bg-white group py-1.5 px-2.5"
          >
            <span className="w-56 h-48 rounded bg-green absolute bottom-0 left-0 translate-x-full ease-out duration-500 transition-all translate-y-full mb-9 ml-9 group-hover:ml-0 group-hover:mb-32 group-hover:translate-x-0"></span>
            <span className="relative text-sm flex flex-row items-center gap-1 w-full text-left text-white transition-colors duration-300 ease-in-out group-hover:text-white">
              Learn more
              <svg className="h-5 w-5 flex-none text-white transform -rotate-90" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                <path fillRule="evenodd" d="M5.23 7.21a.75.75 0 011.06.02L10 11.168l3.71-3.938a.75.75 0 111.08 1.04l-4.25 4.5a.75.75 0 01-1.08 0l-4.25-4.5a.75.75 0 01.02-1.06z" clipRule="evenodd" />
              </svg>
            </span>
          </a>
        </div>
      </div>
    </div>
  );
};

export default CardComponent;
