import React from 'react';
import Swiper from 'swiper';
import 'swiper/swiper-bundle.min.css';
import 'swiper/swiper.min.css';
import './Gallery.css'; // Include the custom styles in a separate CSS file

const images = [
  {
    src: 'https://pagedone.io/asset/uploads/1712568734.png',
    title: 'Golden&Sliver Metal Belt Watch',
    description: 'a chic and understated accessory for any occasion.'
  },
  {
    src: 'https://pagedone.io/asset/uploads/1712569721.png',
    title: 'Golden Metal Belt Watch',
    description: 'a chic and understated accessory for any occasion.'
  },
  {
    src: 'https://pagedone.io/asset/uploads/1712568744.png',
    title: 'Sliver Metal Belt Watch',
    description: 'a chic and understated accessory for any occasion.'
  },
  // Add more images as needed
];

class Gallery extends React.Component {
  componentDidMount() {
    new Swiper('.gallery-top', {
      spaceBetween: 20,
      slidesPerView: 3,
      parallax: true,
      centeredSlides: true,
      loop: true,
      autoplay: true,
      autoplaySpeed: 20,
      slideToClickedSlide: true,
      paginationClickable: true,
      navigation: {
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev'
      },
      breakpoints: {
        1920: {
          slidesPerView: 3,
          spaceBetween: 20,
        },
        1400: {
          slidesPerView: 3,
          spaceBetween: 20,
          centeredSlides: true,
        },
        900: {
          slidesPerView: 3,
          spaceBetween: 15,
          centeredSlides: true,
        },
        200: {
          slidesPerView: 2,
          spaceBetween: 15,
        },
      },
    });
  }

  render() {
    return (
      <section className="py-24">
        <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8 overflow-hidden">
          <div className="pb-16">
            <h2 className="w-full text-center text-gray-900 text-4xl font-bold font-manrope leading-loose pb-2.5">Our Gallery</h2>
            <p className="w-full text-center text-gray-600 text-lg font-normal leading-8">Explore the essence of beauty in our gallery's intimate space.</p>
          </div>
          <div className="mx-auto w-auto relative">
            <button id="slider-button-left" className="top-[35%] swiper-button-prev w-11 h-11 p-3 xl:flex hidden shadow-[0px_1px_2px_0px_rgba(16,_24,_40,_0.05)] items-center justify-center border border-gray-300 rounded-lg group hover:bg-gray-900 transition-all duration-700 ease-in-out">
              <svg className="text-gray-900 group-hover:text-white" xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 20 20" fill="none">
                <path d="M12.5002 14.9999L7.50005 9.99973L12.5032 4.99658" stroke="currentColor" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round" />
              </svg>
            </button>
            <div className="swiper-container gallery-top w-full md:w-[1028px] mx-auto xl:overflow-hidden pt-6">
              <div className="swiper-wrapper w-max mx-auto mb-5 flex gap-3">
                {images.map((image, index) => (
                  <div className="swiper-slide max-w-[319px] grid" key={index}>
                    <img className="grow shrink basis-0 w-full" src={image.src} alt={image.title} />
                    <div className="swiper-box mx-auto relative flex-col justify-center items-center gap-1 flex mt-5">
                      <h5 className="w-full text-center text-gray-900 text-xl font-medium leading-loose">{image.title}</h5>
                      <p className="w-full text-center text-gray-600 text-base font-normal leading-relaxed">{image.description}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <button id="slider-button-right" className="swiper-button-next top-[35%] xl:flex hidden shadow-[0px_1px_2px_0px_rgba(16,_24,_40,_0.05)] w-11 h-11 items-center justify-center border border-gray-300 rounded-lg group  transition-all duration-700 ease-in-out hover:bg-gray-900">
              <svg className="text-gray-900 group-hover:text-white" xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 20 20" fill="none">
                <path d="M7.50301 4.99637L12.5032 9.99657L7.50006 14.9997" stroke="currentColor" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round" />
              </svg>
            </button>
          </div>
        </div>
      </section>
    );
  }
}

export default Gallery;
