import React from 'react';
import { useEffect , useState } from 'react';
import './App.css';
import logo from './logo.png'
import example from './example.jpg';

const StickyHeader = () => {
  const [isSticky, setIsSticky] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.scrollY > 0);
    };

    window.addEventListener('scroll', handleScroll);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className={isSticky ? 'sticky shadow-2' : ''}>
      <h1>Sticky Header</h1>
    </div>
  );
};

function App () {
  return (
    <div className={isSticky ? 'sticky shadow-2' : ''} >
      <StickyHeader />
      <header>
        <div className="{ 'shadow-2xl bg-black} bg-black h-80">
          <nav className='lg:flex hidden mx-auto max-w-7xl items-center bg-white py-2 rounded-md mt-2 ring-1 justify-between px-6'>
          <div class="flex lg:flex-1">
              <a href="/" class="-m-1.5 p-1.5">
                  <span class="sr-only">Your Company</span>
                  <img src={logo} className="h-16 w-auto" alt="logo" />
              </a>
          </div>
            <div className="hidden lg:flex lg:gap-x-12">
                              <a href="/about-us" class="text-base dark:text-black hover:underline hover:underline-offset-4 font-normal text-black">About</a>
                              <a href="/classes" class="text-base dark:text-black hover:underline hover:underline-offset-4 font-normal text-black">Class</a>
                              <a href="/articles-resources" class="text-base dark:text-black hover:underline hover:underline-offset-4 font-normal text-black">Articles &amp; Resources</a>
                              <a href="/events" class="text-base dark:text-black hover:underline hover:underline-offset-4 font-normal text-black">Events</a>
                              <a href="/contact-us" class="text-base dark:text-black hover:underline hover:underline-offset-4 font-normal text-black">Contact Us</a>
                      </div>
          </nav>
        </div>
      </header>  
        <p></p>
      </div>
  );
}

export default App;
